import React, { useState } from "react";
import Axios from "axios";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import notes from "./notes";
import Login from "./Login";
import SignUp from "./SignUp";
import Content from "./content";
import { useEffect } from "react";

function App() {

  useEffect(() => {
    Axios.get("http://localhost:5000/getData")
      .then((response) => console.log(response))
      .catch((e) => console.log(e));
  }, []);

  const [isShowLogin, setIsShowLogin] = useState(false);
  const [isShowSignUp, setIsShowSignUp] = useState(false);
  const [isShowContent, setIsShowContent] = useState(false);
  const [isLoggedIn , setLoginStatus] = useState(false);

  const handleLoginClick = () => {
    setIsShowLogin((isShowLogin) => !isShowLogin);
    setIsShowSignUp(isShowSignUp ? !isShowSignUp : null);
    setIsShowContent(isShowContent ? !isShowContent : null);
    setIsShowLogin(isLoggedIn ? false : true);
  };

  const handleSignUpLink = () => {
    setIsShowSignUp((isShowSignUp) => !isShowSignUp);
    setIsShowLogin(isShowLogin ? !isShowLogin : null);
    setIsShowContent(isShowContent ? !isShowContent : null);
  };

  const handleContent = () => {
    setIsShowContent((isShowContent) => !isShowContent);
    setIsShowLogin(isShowLogin ? !isShowLogin : null);
    setIsShowSignUp(isShowSignUp ? !isShowSignUp : null);
  };
  
  const handleLogin = (data) => {
    console.log(data);
    setLoginStatus(data);
  }
  return (
    <div>
      <Header
        handleLoginClick={handleLoginClick}
        handleSignUpLink={handleSignUpLink}
        handleContent={handleContent}
      />
      {isShowContent ? <Content /> : null}
      {isShowLogin ? <Login onChildData = {handleLogin}/> : null}
      {isShowSignUp ? <SignUp /> : null}
      {isLoggedIn ? <Note/> : <h1>Login to see Your Notes</h1>}

      <Footer />
    </div>
  );
}

export default App;

