import React, { useState } from 'react';
//import Axios from 'axios';
import Header from './Header';
import Footer from './Footer';
import Note from './Note';
import notes from './notes';
import Login from './Login';
import SignUp from './SignUp';
import Content from './content'
import { useEffect } from 'react';

function App() {

  const[backendData,setData] = useState("");
  
  useEffect(() => {
    fetch("/getData").then(data => {setData(data)})
  },[]);
  
  
  const [isShowLogin , setIsShowLogin] = useState(false);
  const [isShowSignUp , setIsShowSignUp] = useState(false);
  const [isShowContent , setIsShowContent] = useState(false);
  
  const handleLoginClick = () => {
    setIsShowLogin((isShowLogin) => !(isShowLogin));
    setIsShowSignUp((isShowSignUp) ? !(isShowSignUp) : null);
    setIsShowContent((isShowContent) ? !(isShowContent) : null);
  }

  const handleSignUpLink = () => {
    setIsShowSignUp((isShowSignUp) => !(isShowSignUp));
    setIsShowLogin((isShowLogin) ? !(isShowLogin) : null);
    setIsShowContent((isShowContent) ? !(isShowContent) : null);
  }

  const handleContent = () => {
     setIsShowContent((isShowContent) => !(isShowContent));
     setIsShowLogin((isShowLogin) ? !(isShowLogin) : null);
     setIsShowSignUp((isShowSignUp) ? !(isShowSignUp) : null);
  }
   
  return <div>
     <Header handleLoginClick = {handleLoginClick} handleSignUpLink = {handleSignUpLink} handleContent = {handleContent}/>
     {isShowContent  ? <Content/> : null}
     {isShowLogin  ? <Login/> : null}
     {isShowSignUp ? <SignUp/> : null}
     {notes.map((noteItem) => {
        return <Note title = {noteItem.title}  content ={noteItem.content} />
     })}
     {backendData}
    
     <Footer />
  </div>
}

export default App;
