import React, { useState } from 'react';
import Header from './Header';
import Footer from './Footer';
import Note from './Note';
import notes from './notes';
import Login from './Login';
import SignUp from './SignUp';

function App() {
  const [isShowLogin , setIsShowLogin] = useState(false);
  const [isShowSignUp , setIsShowSignUp] = useState(false);
  
  const handleLoginClick = () => {
    setIsShowLogin((isShowLogin) => !(isShowLogin));
    setIsShowSignUp((isShowSignUp) ? !(isShowSignUp) : null);
  }

  const handleSignUpLink = () => {
    setIsShowSignUp((isShowSignUp) => !(isShowSignUp));
    setIsShowLogin((isShowLogin) ? !(isShowLogin) : null);
  }
   
  return <div>
     <Header handleLoginClick = {handleLoginClick} handleSignUpLink = {handleSignUpLink} />
     {isShowLogin  ? <Login/> : null}
     {isShowSignUp ? <SignUp/> : null}
     {notes.map((noteItem) => {
        return <Note title = {noteItem.title}  content ={noteItem.content} />
     })}
     <Footer />
  </div>
}

export default App;
